import React, { useState, useEffect } from 'react';

const useFetch = (url) => {
  const [data, setData] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const abortCont = new AbortController();

    const fetchData = async () => {
      try {
        const response = await fetch(url, { signal: abortCont.signal });
        if (!response.ok) {
          throw new Error('Could not fetch the data for that resource');
        }
        const jsonData = await response.json();
        setData(jsonData);
        setIsPending(false);
        setError(null);
      } catch (err) {
        if (err.name !== 'AbortError') {
          setError(err.message);
          setIsPending(false);
        }
      }
    };

    fetchData();

    return () => abortCont.abort(); // cleanup function
  }, [url]);

  return { data, isPending, error };
};

const DeleteFunction = (id, setTodos) => {
  fetch(`http://localhost:3000/to-do/${id}`, {
    method: 'DELETE',
  })
    .then(() => {
      setTodos((prevTodos) => prevTodos.filter((todo) => todo._id !== id));
    })
    .catch((err) => {
      console.log(err);
    });
};

const EditFunction = (id, newData, setTodos) => {
  fetch(`http://localhost:3000/to-do/${id}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(newData),
  })
    .then((res) => {
      if (!res.ok) {
        throw new Error('Could not update the data for that resource');
      }
      return res.json();
    })
    .then((updatedData) => {
      console.log('Data updated successfully', updatedData);
      setTodos((prevTodos) =>
        prevTodos.map((todo) => (todo._id === id ? updatedData : todo))
      );
    })
    .catch((err) => {
      console.log(err);
    });
};

const TodoList = ({ todos, setTodos }) => {
  const [editingTodo, setEditingTodo] = useState(null);
  const [updatedData, setUpdatedData] = useState({
    title: '',
    description: '',
    date: '',
  });

  const handleEdit = (todo) => {
    setEditingTodo(todo);
    setUpdatedData({
      title: todo.title,
      description: todo.description,
      date: todo.date.slice(0, 10),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the EditFunction here to update the data on the server
    EditFunction(editingTodo._id, updatedData, setTodos);
  };

  return (
    <div>
      <div className="paper">
        <div className="lines">
          <div contentEditable>
            <h5>
              <strong>Todo List</strong>
            </h5>
            <ul>
              {todos.map((todo) =>
                todo === editingTodo ? (
                  <li key={todo._id}>
                    <form onSubmit={handleSubmit}>
                      <input
                        type="text"
                        value={updatedData.title}
                        onChange={(e) =>
                          setUpdatedData({
                            ...updatedData,
                            title: e.target.value,
                          })
                        }
                      />
                      <input
                        type="text"
                        value={updatedData.description}
                        onChange={(e) =>
                          setUpdatedData({
                            ...updatedData,
                            description: e.target.value,
                          })
                        }
                      />
                      <input
                        type="date"
                        value={updatedData.date}
                        onChange={(e) =>
                          setUpdatedData({
                            ...updatedData,
                            date: e.target.value,
                          })
                        }
                      />
                      <button type="submit">Save</button>
                      <button type="button" onClick={() => setEditingTodo(null)}>
                        Cancel
                      </button>
                    </form>
                  </li>
                ) : (
                  <li key={todo._id}>
                    <strong>{todo.title}</strong>
                    <ul>
                      <li>
                        <strong>Description: </strong>
                        {todo.description}
                      </li>
                      <li>
                        <strong>Date: </strong>
                        {todo.date.slice(0, 10)}
                      </li>
                      <p className="lbutton">
                        <button onClick={() => handleEdit(todo)}>Edit</button>
                        <button onClick={() => DeleteFunction(todo._id, setTodos)}>
                          Delete
                        </button>
                      </p>
                    </ul>
                  </li>
                )
              )}
            </ul>
          </div>
        </div>
        <div className="holes">
          <div className="hole"></div>
          <div className="hole"></div>
          <div className="hole"></div>
        </div>
      </div>
    </div>
  );
};

const Home = () => {
  const { error, isPending, data: todos } = useFetch('http://localhost:3000/to-do');
  const [todoList, setTodoList] = useState(todos || []);

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {todoList && <TodoList todos={todoList} setTodos={setTodoList} />}
    </div>
  );
};

export default Home;
